mapper.insert:
INSERT INTO board (
Board_Seq,
Board_Writer,
Board_Title,
Board_Content,
Board_Group,
Board_Step,
Board_Depth,
Board_Readcount,
Board_Ip,
Board_Date,
Board_Type
)
values(
BOARD_SEQ.nextval
,'관리자'
,#{BoardTitle}
,#{BoardContent}
,#{BoardGroup}
,#{BoardStep}
,#{BoardDepth}
,#{BoardReadcount}
,#{BoardIp}
,TO_DATE(sysdate,'yyyy.mm.dd')
,#{BoardType}
)

mapper.update:
UPDATE board
SET board_title=#{boardTitle}, board_content=#{boardContent},
board_type=#{boardType}
WHERE board_seq=#{boardSeq}
