mapper.update:
UPDATE MEMBER 
SET MEMBER_STATUS = #{memberStatus}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER 
SET MEMBER_STATUS = "D"
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER 
SET MEMBER_PASS = #{memberPass}, MEMBER_NAME = #{memberName}, 
	MEMBER_EMAIL = #{memberEmail}, MEMBER_STATUS = #{memberStatus}, 
	MEMBER_PHONE = #{memberPhone}
WHERE MEMBER_ID = #{memberId}
